<template>
    <div class="advanced-table" @click="closeDropdown" @keyup.esc="closeDropdown">
        <div class="search">
            <div v-if="showFilter">
                <div class="input-group">
                    <input type="text" class="form-control" placeholder="Filter" v-model="filterKey">

                    <div class="search-icon"></div>
                </div>
            </div>
        </div>

        <div class="column-picker">
            <div v-if="showColumnPicker">
                <div class="btn-group" :class="{'open' : columnMenuOpen}">
                    <button @click.stop.prevent="columnMenuOpen = !columnMenuOpen" @keyup.esc="columnMenuOpen = false" type="button" class="btn btn-default dropdown-toggle" data-toggle="dropdown" aria-haspopup="true">
                        Columns
                    </button>

                    <ul class="dropdown-menu">
                        <li v-for="column in displayCols">
                            <a href="#" @click.stop.prevent="toggleColumn(column)">
                                <span v-if="column.visible" class="icon-ok"></span> {{ column.title }}
                            </a>
                        </li>
                    </ul>
                </div>
            </div>
        </div>

        <div class="table-wrapper">
            <div class="responsive-table">
                <div class="table">
                    <header-row>
                        <table-cell :key="column.name" v-for="column in displayColsVisible" :tabindex="headerTabIndexComputed" @keyup.enter.native="sortBy($event, column.name)" @click.native="sortBy($event, column.name)" track-by="column" :class="getClasses(column)">
                            {{ column.title }}
                        </table-cell>
                    </header-row>

                    <table-row key="id" v-for="entry in filteredValuesSorted " track-by="entry" @click.native="rowClickHandler($event, entry)">
                        <table-cell :key="column.name" v-for="column in displayColsVisible" track-by="column" v-show="column.visible" :class="column.cellstyle">
                            <span v-if="column.renderfunction!==false" v-html="column.renderfunction( column.name, entry )"></span>
                            <span v-else-if="!column.editable"> {{ entry[column.name] }} </span>
                            <value-field-section v-else="" :entry="entry" :columnname="column.name"></value-field-section>
                        </table-cell>
                    </table-row>
                </div>
            </div>
        </div>

        <div v-if="paginated">
            <div class="btn-toolbar" role="toolbar" aria-label="pagination bar">
                <div class="btn-group" role="group" aria-label="first page">
                    <button type="button" class="btn btn-default" @click="page=1">«</button>
                </div>

                <div class="btn-group" role="group" aria-label="pages">
                    <button v-for="index in validPageNumbers" type="button" class="btn btn-default" :class="{ active: page===index }" @click="page=index">{{ index }}</button>
                </div>

                <div class="btn-group" v-if="showPaginationEtc">...</div>

                <div class="btn-group" role="group" aria-label="last page">
                    <button type="button" class="btn btn-default" @click="page=maxPage">»</button>
                </div>
            </div>
        </div>
    </div>
</template>

<script>
