<template>
    <div class="alert">
        <div v-if="!collapsible" :class="{ 'alert-inner': true, 'alert-info': type == 'info', 'alert-success': type == 'success', 'alert-danger': type == 'danger' }">
            <slot></slot>
        </div>

        <div v-if="collapsible" :class="{ 'collapsible': true, 'alert-info': type == 'info', 'alert-success': type == 'success', 'alert-danger': type == 'danger' }">
            <div class="alert-header" :class="{ 'open': content }">
                <span :class="icon"></span>

                <slot></slot>

                <div class="toggle">
                    <button @click="togglingContent" :class="{ 'open': content }"><tooltip position="left">{{ tooltip }}</tooltip><span class="fa fa-chevron-down"></span></button>
                </div>
            </div>

            <div v-if="hasContentSlot" v-show="content" class="alert-content">
                <slot name="content"></slot>
            </div>
        </div>
    </div>
</template>

<script>
"use strict";var Tooltip=use("./Tooltip.vue");module.exports={name:"Alert",components:{Tooltip:Tooltip},data:function data(){return{content:false,icon:""}},props:{collapsible:{type:Boolean,default:false},type:{type:String,default:"info"}},computed:{hasContentSlot:function hasContentSlot(){return!!this.$slots["content"]},tooltip:function tooltip(){if(this.content){return"Collapse"}else{return"Expand"}}},methods:{togglingContent:function togglingContent(){this.content=!this.content}},mounted:function mounted(){if(this.type=="info"){this.icon="fa fa-info-circle"}if(this.type=="success"){this.icon="fa fa-check-circle"}if(this.type=="danger"){this.icon="fa fa-exclamation-triangle"}}};
</script>

